import Link from "next/link";
import styled from "styled-components";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import User from "./User";
import { CURRENT_USER_QUERY } from "./User";

const SIGNOUT_MUTATION = gql`
  mutation SIGNOUT_MUTATION {
    signout {
      SuccessMessage
    }
  }
`;

const NavStyle = styled.nav`
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  justify-content: space-between;
  margin-top: 5%;
  margin-right: 5%;
  a {
    text-align: center;
    margin: 10px;
    font-size: 1.2em;
  }
  a:hover {
    color: red;
  }
`;

const Nav = () => (
  <User>
    {({ data }) => {
      const me = data ? data.me : null;
      return (
        <NavStyle>
          <Link href="/about">
            <a>About</a>
          </Link>
          <Link href="/articles">
            <a>Articles</a>
          </Link>
          {!me && (
            <Link href="/account">
              <a>Account</a>
            </Link>
          )}
          {me && (
            <>
              <Link href="/account">
                <a>{me.name}</a>
              </Link>
              <Link href="/submit">
                <a>Submit</a>
              </Link>
              <Mutation
                mutation={SIGNOUT_MUTATION}
                refetchQueries={[{ query: CURRENT_USER_QUERY }]}
              ></Mutation>
              <Link href="/signout">
                <a>Signout</a>
              </Link>
            </>
          )}
        </NavStyle>
      );
    }}
  </User>
);

export default Nav;
